import React from 'react';
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Container,
  Row,
  Col,
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Card,
  CardTitle,
  CardText,
  ListGroup,
  ListGroupItem
} from 'reactstrap';
import classnames from 'classnames';

import Head from 'next/head';
import SignatureCanvas from 'react-signature-canvas';

class Signature extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      activeTab: '1'
    };
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  render() {
    return (
      <>
        <Head>
          <link rel="stylesheet" href="/static/css/bootstrap.min.css" />
        </Head>

        <Container>
          <Row>
            <Col xs="3">
              <ListGroup>
                <ListGroupItem
                  className={classnames({
                    active: this.state.activeTab === '1'
                  })}
                  onClick={() => {
                    this.toggle('1');
                  }}
                  tag="button"
                >
                  Type it in
                </ListGroupItem>
                <ListGroupItem
                  tag="button"
                  className={classnames({
                    active: this.state.activeTab === '2'
                  })}
                  onClick={() => {
                    this.toggle('2');
                  }}
                >
                  Draw It
                </ListGroupItem>
              </ListGroup>
            </Col>

            {/* Tab Content column */}
            <Col>
              <TabContent activeTab={this.state.activeTab}>
                <TabPane tabId="1">
                  <Row>
                    <Col sm="12">
                      <h2 className="text-center">Create your Signature</h2>
                    </Col>
                  </Row>
                  <Form>
                    <Row>
                      <Col>
                        <FormGroup>
                          <Label for="confirm-name">Confirm your name</Label>
                          <Input
                            type="text"
                            name="name"
                            id="confirm-name"
                            placeholder="Confirm your name"
                          />
                        </FormGroup>
                      </Col>
                      <Col sm="4">
                        <FormGroup>
                          <Label for="confirm-initials">
                            Confirm your initials
                          </Label>
                          <Input
                            type="text"
                            name="initials"
                            id="confirm-initials"
                            placeholder="Confirm your initials"
                          />
                        </FormGroup>
                      </Col>
                    </Row>

                    <Row>
                      <Col>
                        <FormGroup>
                          <Label for="confirm-name">Preview</Label>
                          <span className="name-preview"> test</span>
                        </FormGroup>
                      </Col>
                      <Col sm="4">
                        <FormGroup>
                          <span className="initials-preview"> test</span>
                        </FormGroup>
                      </Col>
                    </Row>
                  </Form>
                </TabPane>
                <TabPane tabId="2">
                  <Row>
                    <Col sm="12">
                      <h2 className="text-center">Create your Signature</h2>
                    </Col>
                  </Row>
                </TabPane>
              </TabContent>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default Signature;
