import React from 'react';
import {
  Container,
  Row,
  Col,
  TabContent,
  TabPane,
  ListGroup,
  ListGroupItem,
  Form
} from 'reactstrap';
import classnames from 'classnames';

import Head from 'next/head';
import SignatureCanvas from 'react-signature-canvas';

import TypeItIn from '../components/typeItIn';

class Signature extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      activeTab: '1'
    };
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  render() {
    return (
      <>
        <Head>
          <link rel="stylesheet" href="/static/css/bootstrap.min.css" />
        </Head>

        <Container>
          <Row>
            <Col xs="3">
              <ListGroup>
                <ListGroupItem
                  className={classnames({
                    active: this.state.activeTab === '1'
                  })}
                  onClick={() => {
                    this.toggle('1');
                  }}
                  tag="button"
                >
                  Type it in
                </ListGroupItem>
                <ListGroupItem
                  tag="button"
                  className={classnames({
                    active: this.state.activeTab === '2'
                  })}
                  onClick={() => {
                    this.toggle('2');
                  }}
                >
                  Draw It
                </ListGroupItem>
              </ListGroup>
            </Col>

            {/* Tab Content column */}
            <Col>
              <TabContent activeTab={this.state.activeTab}>
                <TabPane tabId="1">
                  <TypeItIn />
                </TabPane>
                <TabPane tabId="2">
                  <Row>
                    <Col sm="12">
                      <h2 className="text-center">Create your Signature</h2>
                    </Col>
                  </Row>
                  <Form>
                    <Row>
                      <Col>Draw Signature</Col>
                    </Row>

                    <Row>
                      <Col>
                        <SignatureCanvas
                          penColor="black"
                          backgroundColor="#CCCCCC"
                          canvasProps={{
                            width: 700,
                            height: 150,
                            className: 'sigCanvas'
                          }}
                        />
                      </Col>
                    </Row>

                    <Row>
                      <Col>Draw Signature</Col>
                    </Row>

                    <Row>
                      <Col>
                        <SignatureCanvas
                          penColor="black"
                          backgroundColor="#CCCCCC"
                          canvasProps={{
                            width: 700,
                            height: 150,
                            className: 'sigCanvas'
                          }}
                        />
                      </Col>
                    </Row>
                  </Form>
                </TabPane>
              </TabContent>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default Signature;
