import React from 'react';
import { Button, Form, FormGroup, Label, Input, Row, Col } from 'reactstrap';

class DrawIt extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      initialsName: '',
      nameFont: ''
    };
  }
  render() {
    return (
      <>
        <Row>
          <Col sm="12">
            <h2 className="text-center" style={{ marginBottom: '30px' }}>
              Create your Signature
            </h2>
          </Col>
        </Row>
        <Form>
          <Row>
            <Col>Draw Signature</Col>
          </Row>
          <Row>
            <Col>
              <SignatureCanvas
                penColor="black"
                backgroundColor="#CCCCCC"
                canvasProps={{
                  width: 700,
                  height: 150,
                  className: 'sigCanvas'
                }}
              />
            </Col>
          </Row>
          <Row>
            <Col>Draw Initials</Col>
          </Row>
          <Row>
            <Col>
              <SignatureCanvas
                penColor="black"
                backgroundColor="#CCCCCC"
                canvasProps={{
                  width: 250,
                  height: 150,
                  className: 'sigCanvas'
                }}
              />
            </Col>
          </Row>
          <Row>
            <Col>
              <p>
                By clicking Accept and Sign, I agree that the signature wil be
                the electric representation of my signature for all purposewhen
                I for my agend use themon documents. Including legallybinding
                documnets, just the same as pen and paper signature.{' '}
              </p>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button color="primary float-right">Accept and Sign</Button>
            </Col>
          </Row>
        </Form>
      </>
    );
  }
}

export default DrawIt;
