import React from 'react';
import {
  Container,
  Row,
  Col,
  TabContent,
  TabPane,
  ListGroup,
  ListGroupItem
} from 'reactstrap';
import classnames from 'classnames';

import Head from 'next/head';

import TypeItIn from '../components/typeItIn';
import DrawIt from '../components/drawIt';

class Signature extends React.Component {
  constructor(props) {
    super(props);
    this.sigCanvas = React.createRef();
    this.state = {
      activeTab: '1',
      name: '',
      initialsName: '',
      nameFont: ''
    };
  }

  toggle = tab => {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  };

  nameChangeHandler = event => {
    this.setState({ name: event.target.value });
  };

  initialsNameChangeHandler = event => {
    this.setState({ initialsName: event.target.value });
  };

  changeFontHandler = event => {
    this.setState({ nameFont: event.target.value });
  };
  render() {
    console.log('[name]', this.state.name);
    return (
      <>
        <Head>
          <link rel="stylesheet" href="/static/css/bootstrap.min.css" />
        </Head>

        <Container>
          <Row>
            <Col xs="3">
              <ListGroup>
                <ListGroupItem
                  className={classnames({
                    active: this.state.activeTab === 'type-it-in'
                  })}
                  onClick={() => {
                    this.toggle('type-it-in');
                  }}
                  tag="button"
                >
                  Type it in
                </ListGroupItem>
                <ListGroupItem
                  tag="button"
                  className={classnames({
                    active: this.state.activeTab === 'draw-it'
                  })}
                  onClick={() => {
                    this.toggle('draw-it');
                  }}
                >
                  Draw It
                </ListGroupItem>
              </ListGroup>
            </Col>

            {/* Tab Content column */}
            <Col>
              <TabContent activeTab={this.state.activeTab}>
                <TabPane tabId="type-it-in">
                  <TypeItIn
                    nameChangeHandler={this.nameChangeHandler}
                    initialsNameChangeHandler={this.initialsNameChangeHandler}
                    changeFontHandler={this.changeFontHandler}
                    nameFont={this.state.nameFont}
                    name={this.state.name}
                    initialsName={this.state.initialsName}
                  />
                </TabPane>
                <TabPane tabId="draw-it">
                  <Row style={{ marginBottom: '30px' }}>
                    <Col sm="12">
                      <h2 className="text-center">Create your Signature</h2>
                    </Col>
                  </Row>
                  <DrawIt dataRef={this.sigCanvas} />
                </TabPane>
              </TabContent>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default Signature;
